<?php
function msg($data, $mode = 1, $debug = false) {
        $base_img = '';
        switch ($mode) {
            case 1: $color[0] = '00529B'; $color[1] = 'BDE5F8'; $img = $base_img.''; break;
            case 2: $color[0] = '4F8A10'; $color[1] = 'DFF2BF'; $img = $base_img.''; break;
            case 3: $color[0] = '9F6000'; $color[1] = 'FEEFB3'; $img = $base_img.''; break;
            case 4: $color[0] = 'D8000C'; $color[1] = 'FFBABA'; $img = $base_img.''; break;
        }

        if($debug) {
            $pre  = print_r($data,true);
            $html = "<pre>".htmlentities($pre)."</pre>";
        } else {
            $html = $data;
        }

		echo "<div style='color: #".$color[0].";background-color: #".$color[1].";background-repeat: no-repeat;background-position: 10px center;background-image:url(\"data:image/png;base64,".$img."\");border: 1px solid;margin: 10px 0px;padding:15px 10px 15px 50px;'>".$html."</div>";
	}